/* Animations Shared */
.fade-in-up {
    animation: fadeInUp 0.5s ease-out forwards;
}

.ripple {
    position: absolute;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.4);
    transform: scale(0);
    animation: ripple 0.6s linear;
    pointer-events: none;
}

