cat >> /Users/alessiodepreytere/Documents/GitHub/DelovaHomeWeb/style/base.css <<EOF

/* Redesigned Weather Widget */
.weather-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 10px;
    text-align: center;
    position: relative;
    min-height: 160px;
}

.weather-main {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 20px;
    margin-bottom: 15px;
}

.weather-icon-large {
    font-size: 3.5rem;
    filter: drop-shadow(0 4px 6px rgba(0,0,0,0.1));
    transition: transform 0.3s ease;
}

.weather-icon-large:hover {
    transform: scale(1.1) rotate(5deg);
}

.weather-temp-large {
    font-size: 3.2rem;
    font-weight: 800;
    line-height: 1;
    letter-spacing: -2px;
}

.weather-desc-text {
    font-size: 1.1rem;
    font-weight: 500;
    margin-bottom: 15px;
    text-transform: capitalize;
    opacity: 0.9;
}

.weather-details {
    display: flex;
    gap: 15px;
    font-size: 0.85rem;
    background: rgba(127, 127, 127, 0.1);
    padding: 8px 16px;
    border-radius: 20px;
    backdrop-filter: blur(5px);
}

.weather-detail-item {
    display: flex;
    align-items: center;
    gap: 6px;
}

.weather-location-btn {
    position: absolute;
    top: -5px;
    right: -5px;
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    background: rgba(127, 127, 127, 0.1);
    opacity: 0.6;
    cursor: pointer;
    transition: all 0.2s;
}

.weather-location-btn:hover {
    opacity: 1;
    background: rgba(127, 127, 127, 0.2);
    transform: rotate(15deg);
}

/* Weather Icon Colors */
.weather-icon-sun { color: #FFD700; }
.weather-icon-cloud { color: #A0A0A0; }
.weather-icon-rain { color: #4FC3F7; }
.weather-icon-snow { color: #E0F7FA; }
.weather-icon-storm { color: #7E57C2; }
EOF
